package actores;import java.awt.Point;import java.util.ArrayList;/** * @author milver */public class Moved{	private int rejilla;    private ArrayList<Point> puntosLineal;    public Moved(){    	rejilla=1;        puntosLineal=new ArrayList<Point>();    }    private boolean repetido(int y){    	boolean res=true;		if (puntosLineal.size()>0) {			if (puntosLineal.get(puntosLineal.size()-1).y==y){				res=false;			}		}    	return res;    }    private void generarPuntos(float xi,float yi,float xf,float yf){        float m=(yf-yi)/(xf-xi);        if (xi<xf) {            while (xi<xf) {            	if (repetido((int)getPuntoY((xi), m, xf, yf))) {            		puntosLineal.add(new Point((int)(xi), (int)getPuntoY((xi), m, xf, yf)));				}                xi=xi+getRejilla();            }            puntosLineal.add(new Point((int)xf,(int)yf));        }        else{            while (xi>xf) {            	if (repetido((int)getPuntoY((xi), m, xf, yf))) {                    puntosLineal.add(new Point((int)(xi), (int)getPuntoY((xi), m, xf, yf)));					}                xi=xi-getRejilla();            }            puntosLineal.add(new Point((int)xf,(int)yf));        }    }    private float getPuntoY(float xi,float m,float xf,float yf){        return (yf-(m*(xf-xi)));    }    public ArrayList<Point> getPointsDiagonal(int xi,int yi,int xf,int yf){        generarPuntos(xi, yi, xf, yf);        return puntosLineal;    }    public int getRejilla() {		return rejilla;	}	public void setRejilla(int rejilla) {		this.rejilla = rejilla;	}	public static void main(String[] args) {        Moved mo=new Moved();        System.out.println(mo.getPointsDiagonal(5, 1,28, 8));        System.out.println(mo.parabola(300,200));    }	public ArrayList<Point> parabola(int vx,int vy){    	ArrayList<Point> puntosParabola=new ArrayList<Point>();    	final int fx=vx,fy=vy;        int t=20;        for(int h=0;h<t;h++){            int auxx=t-h;            puntosParabola.add(new Point((fx-auxx*12), (fy+auxx*auxx)));        }        for(int h=0;h<=t;h++){            int auxx=h;            puntosParabola.add(new Point((fx+auxx*12), (fy+auxx*auxx)));        }        return puntosParabola;    }	public ArrayList<Point> circulo(int px,int py,int r) {		ArrayList<Point>puntosCirculo=new ArrayList<>();		for (int i = 0; i < px-r; i++) {					}		return puntosCirculo;	}					}